inbound.set-body:
@{
    string messagepayload = "{ name: 'someone' }"; 
    string[] value;
    if (context.Request.Headers.TryGetValue("Name", out value))
    {
        if(value != null && value.Length > 0 && !String.IsNullOrEmpty(value[0]))
        {
            messagepayload = "{ name: '" + value[0] +"' }"; 
        }
    }
    return messagepayload;
}
